mapper.insert:
INSERT INTO ATTEND (ATTEND_NO, ATTEND_STATUS, SESSION_NO, STUDENT_NO) SELECT SEQ_ANO.NEXTVAL, t.attend_status, t.session_no, t.student_no FROM (
SELECT #{attendMap.attend.attendStatus} AS attend_status, #{attendMap.attend.sessionNo} AS session_no, #{attendMap.attend.studentNo} AS student_no FROM DUAL
) t

mapper.select:
SELECT U.ATTEND_UPDATE_NO, U.ATTEND_UPDATE_CONTENT, U.CORRECTION_APPLICATION_DATE, U.STATUS, U.REFUSAL, ST.MEMBER_NAME AS studentName, CASE WHEN F.ATTEND_UPDATE_FILE_NO IS NULL THEN 'N' ELSE 'Y' END AS hasFile FROM ATTEND_UPDATE U /* 출결 → 학생 */ JOIN ATTEND A ON U.ATTEND_NO = A.ATTEND_NO /* 학생 ↔ 반 */ JOIN CLASS_STUDENT CS ON A.STUDENT_NO = CS.STUDENT_NO /* 학생(Member) */ JOIN MEMBER ST ON CS.MEMBER_NO = ST.MEMBER_NO JOIN LECTURE_CONTROL LC ON A.SESSION_NO = LC.CONTROLL_NO JOIN CLASS_LECTURE CL ON LC.CLASS_LECTURE_NO = CL.CLASS_LECTURE_NO JOIN LECTURE L ON CL.LECTURE_NO = L.LECTURE_NO /* 첨부파일 */ LEFT JOIN ATTEND_UPDATE_FILE F ON U.ATTEND_UPDATE_NO = F.ATTEND_UPDATE_NO
/* ★ 강사 번호로 필터링 (로그인한 강사) */ WHERE L.MEMBER_NO = #{teacherNo}
ORDER BY U.ATTEND_UPDATE_NO DESC
