mapper.insert:
INSERT INTO ATTEND (ATTEND_NO, ATTEND_STATUS, SESSION_NO, STUDENT_NO) SELECT SEQ_ANO.NEXTVAL, t.attend_status, t.session_no, t.student_no FROM (
SELECT #{attendMap.attend.attendStatus} AS attend_status, #{attendMap.attend.sessionNo} AS session_no, #{attendMap.attend.studentNo} AS student_no FROM DUAL
) t

mapper.select:
SELECT U.ATTEND_UPDATE_NO, U.ATTEND_UPDATE_CONTENT, U.CORRECTION_APPLICATION_DATE, U.STATUS, U.REFUSAL, U.ATTEND_NO, U.ATTEND_STATUS,
F.ATTEND_UPDATE_FILE_NO, F.ATTEND_UPDATE_FILE_NAME, F.ATTEND_UPDATE_FILE_ORI_NAME, F.FILE_PATH
FROM ATTEND_UPDATE U LEFT JOIN ATTEND_UPDATE_FILE F ON U.ATTEND_UPDATE_NO = F.ATTEND_UPDATE_NO WHERE U.ATTEND_UPDATE_NO = #{attendUpdateNo}
