mapper.update:
UPDATE COUNSEL_APPLICATION
SET STATUS = #{status},
REFUSAL = #{refusal}
WHERE COUNSEL_APPLICATION_NO = #{consultAppNo}

UPDATE COUNSEL_APPLICATION
SET STATUS = 'DONE'
WHERE COUNSEL_APPLICATION_NO = #{consultAppNo}

mapper.insert:
INSERT INTO CONSULTATION (
CONSULTATION_NO,
CONSULTATION_CONTENT,
COUNSEL_APPLICATION_NO
)
VALUES (
SEQ_CNO.NEXTVAL,
#{consultContent},
#{consultAppNo}
)
